package JavaBasics;

public class PrimitiveDataTypes {
	
	private short s;
	private int i;
	private long l;
	private float f;
	private double d;
	private char ch;
	private String str;
	private boolean b;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		PrimitiveDataTypes obj=new PrimitiveDataTypes();
		
		System.out.println ("\n short s =" + obj.s);
		System.out.println ("\n int i =" + obj.i);
		System.out.println ("\n long l =" + obj.l );
		System.out.println ("\n float f =" + obj.f);
		System.out.println ("\n double d =" + obj.d);
		System.out.println ("\n char c =" + obj.ch);
		System.out.println ("\n String s =" + obj.str);
		System.out.println("\n boolean b =" + obj.b);

	}

}
